import React, { useState } from 'react';

const search = (props) => {
 const [product, setProduct] = useState([
      {
      "id" : 1,
    "title" : "Dove sampoo",
    "imageLink" : "https://m.media-amazon.com/images/I/419VHUAK9aL._SL1000_.jpg", 
      },
    { 
    "id" : 2,
    "title" : "Lux Soap",
    "image" : 'https://www.jiomart.com/images/product/600x600/490002473/lux-international-creamy-perfection-bar-soap-125-g-2-20210111.jpg',
    },
    {
    "id" : 3,
    "title" : "secret deodorant",
    "image" : "https://images.ctfassets.net/w9l9p5870v5o/16NnQLmYgLdwM2ZBEW0lQz/9699b12f318dfdedf1ff01456fab6e51/00037000500315_C1N1_NA.png",
    },
    {
      "id" : 4,
      "title" : "wheat",
      "image" : "https://yaffa-cdn.s3.amazonaws.com/yaffadsp/images/dmImage/StandardImage/wheat_3553D7E0-FAA2-11E3-BF7E005056A302E6.jpg",
    }
  ])


  handleOnChange = (e) => {
   if(e.target.value===""){
     setProduct(product)
     return
    }
    const searchResult = product.filter(item=>item.name.toLowercase().startwith(e.target.value.toLowerCase))
    setProduct(searchResult)
  }

  return (
    <>
  <div className="text-center">
      <nav className="navbar navbar-light bg-light">
  <div className="container-fluid">
    <form className="d-flex">
      <input onClick={handleOnChange}className="form-control me-2" type="search" placeholder="Search Products" row="8"aria-label="Search"/>
      <div>
           {
             product.map(item => {
               return(
              <ul>
               <li>{item.title}</li>
                <li>{item.id}</li>
                <li>{item.image}</li>
                </ul>
               )

             })
}
      </div>
    </form>
  </div>
</nav>
  </div>
  </>
  )
};

export default search;
