import React, { useState } from 'react';

const Search = (props) => {



  const handleOnChange = (e) => {
   if(e.target.value===""){
     setProduct(product)
     return
    }
    const searchResult = product.filter(item=>item.name.toLowercase().startwith(e.target.value.toLowerCase))
    setProduct(searchResult)
  }

  return (
    <>
  <div className="text-center">
      <nav className="navbar navbar-light bg-light">
  <div className="container-fluid">
    <form className="d-flex">
      <input onClick={handleOnChange}className="form-control me-2" type="search" placeholder="Search Products" row="8"aria-label="Search"/>
      <div>
           {
             product.map(item => {
               return(
              <ul>
               <li>{item.title}</li>
                <li>{item.id}</li>
                <li>{item.image}</li>
                </ul>
               )

             })
}
      </div>
    </form>
  </div>
</nav>
  </div>
  </>
  )
};

export default Search;
